import { Fragment, useEffect, useState } from "react";
import MainItem from "../../components/mainItem";
import axios from "axios";
import { baseUrl } from "../../store/key";

const Main = () => {

    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    useEffect(() => {
        setLoading(true)
        setData([])
        axios.get(`${baseUrl}/topstories.json`)
        .then(async (res) => {
            const arr = []
            for(let i = 0; i < 100; i++) {
                const itemResponse = await axios.get(`${baseUrl}/item/${res.data[i]}.json`);
                const itemData = itemResponse.data;
                arr.push(itemData)
            }
            setData(arr)
            setLoading(false)
        })
        .catch((e) => {
            setLoading(false)
        })
      }, []);
    return ( 
        <article >

            <section className="main">
        {loading && <div className="loading">LOADING...</div>} 
            {data.map(({id, score, by, time, title, kids}) => (
                <Fragment key={id}>
                    <MainItem id={id} title={title} score={score} by={by} time={time} comments={kids}/>
                </Fragment>
            ))}
            </section>
        </article>
     );
}

export default Main;
